import { createSlice , createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";


export interface user {
    firstName: string;
    middleName: string;
    lastName: string;
    email: string;
    phone: number;
    role: string;
    address: string;
  }
  
  interface IIS {
    users: any;
    error: string;
  }
  
  const initialState: IIS = {
    users: [],
    error: "",
  };

export const updateUser = createAsyncThunk("users/updateUser" , async (data:any) => {
    let baseUrl: string = "http://localhost:8001/"
    // console.log("data" , data);
    // console.log("id" , id);
    // return await axios.patch(baseUrl + `updateuser/${id}` , data)
    
});


const updateSlice = createSlice({
    name:"postSlice",
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(updateUser.pending , (state) => {
            return state
        })
        
        builder.addCase(updateUser.fulfilled , (state , action) => {
            state.users = action.payload
            state.error = "There has been a error"
        })
        builder.addCase(updateUser.rejected , (state , action) => {
            state.users = []
            state.error = action.error.message || "There has been a error"
        })

    }
})

export default updateSlice.reducer
