  firstName: string;
  middleName: string;
  lastName: string;
  email: string;
  phone: number;
  role: string;
  address: string;
}

interface IIS {
  users: any;
  error: string;
}

const initialState: IIS = {
  users: [],
  error: "",
};

export const updateUser = async (data: any, id: any) => {
  let baseUrl: string = "http://localhost:8001/";
  // console.log("data" , data);
