import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import axios from "axios";
import { IIS, user } from "../../Interface/Interface";
import { initialState } from "../__State__/InitialState";
// defining the initial State



export const getCustomer = createAsyncThunk("customer/getCustomer", async () => {
  let baseUrl = "http://localhost:3001/";
  return await axios
    .get(baseUrl + "customers")
    .then((response) => response.data)
    .catch((error) => console.log(error));
});


const customer = createSlice({
  name: "getSlice",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(getCustomer.pending, (state:IIS) => {
      state.loading = true;
    });
    builder.addCase(getCustomer.fulfilled, (state, action: PayloadAction<user[]>) => {
      state.loading = false;
      state.users = action.payload;
      state.error = "";
    });
    builder.addCase(getCustomer.rejected, (state:IIS, action) => {
      state.loading = false;
      state.users = [];
      state.error = action.error.message || "SomeThing Went Wrong";
    });
  },
});

export default customer.reducer;
// export default.getSliceActions  getSlice.actions
