import React from "react";
import { Link } from "react-router-dom";
import { useFormik } from 'formik';

function Auth() {

  // signup
  const formik = useFormik({
    initialValues: {
      name: '',
    },
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  return (
    <div>
     
     <ul className="nav nav-tabs" id="myTab" role="tablist">
  <li className="nav-item" role="presentation">
    <button className="nav-link active" id="home-tab" data-bs-toggle="tab" data-bs-target="#home" type="button" role="tab" aria-controls="home" aria-selected="true">SignUp</button>
  </li>
  <li className="nav-item" role="presentation">
    <button className="nav-link" id="profile-tab" data-bs-toggle="tab" data-bs-target="#profile" type="button" role="tab" aria-controls="profile" aria-selected="false">Login</button>
  </li>

</ul>
<div className="tab-content" id="myTabContent">
  <div className="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">
  <form onSubmit={formik.handleSubmit}>
    <div>

       <label htmlFor="email">Name</label>
       <input
         id="email"
         name="name"
         type="text"
         onChange={formik.handleChange}
         value={formik.values.name}
         className="form-control"
         />
         
         </div>

    <div>

       <label htmlFor="email">Email</label>
       <input
         id="email"
         name="email"
         type="email"
         onChange={formik.handleChange}
         value={formik.values.email}
         className="form-control"
         />

         </div>
 
       <button type="submit">Submit</button>
     </form>
  </div>
  <div className="tab-pane fade" id="profile" role="tabpanel" aria-labelledby="profile-tab">...</div>
</div>

    </div>
  );
}

export default Auth;
