import axios from "axios";
import React, { useEffect , useState } from "react";
import { Link } from "react-router-dom";
import { useAppDispatch, useAppSelector } from "../../app/hooks";
import { getUser, user } from "../../features/Get/Get";
import "./Table.css";
// toastify
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Table() {
  const user = useAppSelector((state) => state.getSlice);
  console.log(user);

  const [userDeleted, setuserDeleted] = useState<boolean>(false)

  const dispatch = useAppDispatch();
  useEffect(() => {
    dispatch(getUser());
  }, [userDeleted]);

  const deleteUser = async (id: string) => {
    // console.log(id);
    alert("Are you Sure you want to delete")
    
   await axios.delete(`http://localhost:8001/deleteuser/${id}`)
    .then(response => {
      if (response.status !== 4001) {
        toast("Del")
      }
    })
    .catch(error => console.log(error))

   
  }

  return (
    <div>

      <table className="table table-striped">
        <thead className="thead-dark">
          <tr>
            <th>ID</th>
            <th scope="col">First Name</th>
            <th scope="col">Middle Name</th>
            <th scope="col">Last Name</th>
            <th scope="col">Email</th>
            <th scope="col">Phone</th>
            <th scope="col">Role</th>
            <th scope="col">Address</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>

        <tbody>
          {/* check if loading is true */}
          <tr>{user.loading ? <div>Loading...</div> : null}</tr>

          {/* check if there is an error */}
          <tr> {!user.loading && user.error ? <div> {user.error} </div> : null}</tr>
          {user.users
            ? user.users.map((element: any) => {
                return (
                  <tr>
                    <th scope="row">{element.id}</th>
                    <td>{element.firstname}</td>
                    <td>{element.middlename}</td>
                    <td>{element.lastname}</td>
                    <td>{element.email}</td>
                    <td>{element.phone}</td>
                    <td>{element.role}</td>
                    <td>{element.address}</td>
                    <td>
                      <button className="mx-3 btn btn-info">
                        <Link to={`/edit/${element.id}`}>
                          Edit
                        </Link>
                        </button>
                      <button className="mx-3 btn btn-danger" onClick={() => deleteUser(`${element.id}`)}>Delete</button>
                    </td>
                  </tr>
                );
              })
            : null}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
