import React, { useEffect, useState } from "react";
import { useFormik } from "formik";
import axios from "axios";
import { useParams } from "react-router-dom";

function EditCustomer() {

  // get customer details according to id
  const [customer, setCustomer] = useState([])

  const { id } = useParams()
  console.log("id is", id);
  

  useEffect( () => {
     const getCustomerData = async () => {
       let baseUrl = "http://localhost:3001/"
       await axios.get(baseUrl + `customers/${id}`)
       .then((response) => setCustomer(response.data))
       .catch((error) => console.log(error)
       )
       await console.log(customer);
     }
 
     getCustomerData()
     
  }, [])
console.log("customer" , customer);


  // formik
  const formik = useFormik({
    initialValues: {
      firstName: 'customer[0]',
      lastName: "",
      email: "",
    },
    enableReinitialize: true,
        isInitialValid: true,


    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  return (
    <>
    
    <h2 className="text-center">Edit Customer</h2>
    <form className="w-50 mx-auto" onSubmit={formik.handleSubmit}>
 

  <div className="form-group">
  <label htmlFor="firstName">First Name</label>
        <input
          id="firstName"
          name="firstName"
          type="text"
          onChange={formik.handleChange}
          value={formik.values.firstName}
          className="form-control"
        />
  </div>
  <div className="form-group">
  <label htmlFor="lastName">Last Name</label>
        <input
          id="lastName"
          name="lastName"
          type="text"
          onChange={formik.handleChange}
          value={formik.values.lastName}
          className="form-control"
        />
    </div>
  <div className="form-check">
  <label htmlFor="email">Email Address</label>
        <input
          id="email"
          name="email"
          type="email"
          onChange={formik.handleChange}
          value={formik.values.email}
          className="form-control"
        />
    </div>
  <button type="submit" className="btn btn-dark my-3 col-md-12">Submit</button>
</form>
    </>
  );
}

export default EditCustomer;
